(8, 'twilio', '{"sid":"ACe319211b71562acfd61915473fad25aa","token":"533d49e2c1bee510bea10f63bb4eaa72","phone_no":"+17147865719"}', '', 0, 1),
(9, 'attendance-tracker', 'Salam,\r\n\r\nThis is to inform you that your child, -student_name- was marked absent in\r\none or more classes today without the administration receiving a prior\r\nnotice of the absence.\r\n\r\nWe encourage you to please fill out the absence form found on the parent\r\ndashboard to let us know so we can make arrangements for homework,\r\nassignments, etc. as we don''t want your children to fall behind in their\r\nclasses.\r\n\r\nWe advise you to please to contact the teachers to obtain all the\r\ninformation that was discussed in class today.\r\n\r\nWasalam\r\n\r\nJazakAllah,\r\n\r\nNausheen Mithani\r\nAdministrator, Jafaria Education Center\r\nEmail. school@jafaria.org\r\nWeb. www.jafariaschool.org', 'Attendance Tracker', 0, 0),
(10, 'tardy-tracker-sms', 'Salam\nYour child, --student_name-- was marked tardy today and this is tardy #--tardy_no--\nPlease ensure students are brought on time in the future.', 'Tardy Tracker SMS', 0, 0),
(11, 'handbook-notification-email', 'Dear Parents,\n\nWe have a new Parent / Student Handbook Agreement that you need to sign. The agreement must be completed and signed.\n \nPlease visit your dashboard at http://jafariaschool.org/student</a> to read the handbook and submit your agreement.\n', 'Handbook Notification Email', 0, 0),
(12, 'report-card-email', 'Dear -parent_name-,\nSalam,\n\nAs many of know, we had our Midterms a few weeks ago as we wrapped up our first semester. \n\nAlhamdulillah, our very first semester at Jafaria Education Center completed successfully with the help and the blessings of the Ahlulbayt (AS) and the support from all of the parents, teachers, and volunteers.\n\nOn the bottom of this email, you will find a link to the Report Card. Please read the first page of the report card carefully as that has a legend for the comments and explains our grading scale.\n\nPlease let us know if there are any questions or concerns\n\nWasalam\n\nLink attachment below :\n-link_report_card-', 'Report Card Email', 0, 0),
(13, 'report-card-finalize', '', 'Report Card Finalize', 0, 1),
(14, 'report-card-teacher', 'Dear Teacher,\n\nYou have not yet finalized the grades for the following classes:\n\n-list_of_the_classes-\n\nPlease finalize them by February 18th, 2015', 'Report Card Teacher', 0, 0),
(15, 'schedule-email-parent', 'Salam,\n\nAs many of you already know, we are holding our first ParentTeacher conference tomorrow.\n\nThanks to all those who signed up for your time slots with the teachers. The following is a schedule of all the meetings you have scheduled\n-list_schedule-\n\nPlease let us know if you have any questions and/or concerns\n\nWasalam', 'Schedule Email Parent', 0, 0),
(16, 'schedule-email-teacher', 'Salam,\n\nAs many of you already know, we are holding our first Parent/Teacher conference tomorrow.\n\nWe gave the parents an option to sign up for 10 minute time slots for each teacher. The following is your schedule for tomorrow. Keep in mind if you have any open slots, there maybe parents that have not signed up through the dashboard present at the event.\n\n-list_schedule-\n\nWe ask all teachers be at the Sunday School no later than 10:30 AM in the morning. Thank  you for your continued dedication and commitment to this great cause. \n\nPlease let us know if you have any questions and/or concerns\n\nWasalam\n\n\nThanks', 'Schedule Email Teacher', 0, 0);
